byHidAndDate.map:
function (doc) {
  var prefix = doc.data.Date.match(/^[^\s]+\s/);
    if(prefix && doc._id.indexOf('ShoppingCart_')===0 && !doc.data.Canceled){
      var hid = doc.data.HardwareId;
      var splitPref = prefix[0].trim().split("/");
      var dateTk = splitPref[2]+ splitPref[1]+splitPref[0];
      emit([hid,dateTk],doc);
    }
}
